import groovy.json.JsonSlurper
class GlobalContext { def static global_context = [:] }
def l_content = context.httpResponse.responseContent def l_slurped_json = new JsonSlurper().parseText(l_content) Object.mixin GlobalContext testRunner.testCase.testSuite.project.setPropertyValue("Security Updates - Change Password",l_slurped_json[0].token) testRunner.testCase.testSuite.project.setPropertyValue("proxy_number",l_slurped_json[0].scope.data_scope.proxy_number) testRunner.testCase.testSuite.project.setPropertyValue("product_id",l_slurped_json[0].scope.data_scope.product_id) testRunner.testCase.testSuite.project.setPropertyValue("accessor_id",l_slurped_json[0].scope.data_scope.accessor_id) assert l_slurped_json[0].token !="" && l_slurped_json[0].token != null

con:config.con:request:
{
  "authorization_name": "Security Updates",
  "identity": {
    "identity_name": "Owner of User Data and DOB Data",
    "authentications": [
      {
        "authentication_name": "User_data", "authentication_data": {
        "public_data": {
          "username": "${=testRunner.testCase.testSuite.project.getPropertyValue("username")}"
        },
        "private_data": {
          "password": "${=testRunner.testCase.testSuite.project.getPropertyValue("password")}"
        }}
      },
      {
        "authentication_name": "DOB_data", "authentication_data": {
        "public_data": {
          "proxy_number": "${=testRunner.testCase.testSuite.project.getPropertyValue("proxy_number")}"
        },
        "private_data": {
          "DOB": "${=testRunner.testCase.testSuite.project.getPropertyValue("DOB")}"
        }}
      }
    ]
  },
  "scope": {
    "scope_name": "Change Security Answers"
  },
  "prerequisite_authorization": {
     "token": "${=testRunner.testCase.testSuite.project.getPropertyValue("Read - Main Screen")}"
  }
}

