import groovy.json.JsonSlurper
class GlobalContext { def static global_context = [:] }
def l_content = context.httpResponse.responseContent def l_slurped_json = new JsonSlurper().parseText(l_content) Object.mixin GlobalContext testRunner.testCase.testSuite.project.setPropertyValue("Secured Demographic Updates - Update Phone",l_slurped_json[0].token) testRunner.testCase.testSuite.project.setPropertyValue("proxy_number",l_slurped_json[0].scope.data_scope.proxy_number) testRunner.testCase.testSuite.project.setPropertyValue("product_id",l_slurped_json[0].scope.data_scope.product_id) testRunner.testCase.testSuite.project.setPropertyValue("accessor_id",l_slurped_json[0].scope.data_scope.accessor_id) assert l_slurped_json[0].token !="" && l_slurped_json[0].token != null

