	log.warn(context.httpResponse.URL.toString() + ">>>>>" + context.httpResponse.requestContent.replace("\n", "").replace("\r", ""))
     log.warn(context.httpResponse.responseContent.replace("\n", "").replace("\r", ""))
}
}

